Test.Config:
searchd
{

}

index test_idx
{
	type			= template

      	min_word_len	= 1
      	min_word_len	= 3


      	stopwords		= /stopwords.txt



      	exceptions		= /synonyms.txt 



	ngram_len		= 1
	ngram_chars		= U+3000..U+2FA1F
}

Test.Custom_test:
$docs = array
(
	"ab ab ab ab ab ab ab ab ab ab ab MS",
	"   this,   is .    MS windows....?",
	"this  ,  is a MS but not Windows",
	"this is the Microsoft  Windows , ,",
	"pro\xEF\xAC\x81ts, lost savings" // latin small ligature 'fi', oh my
);

$words = "MS|Windows";

$opts = array
(
	"before_match" => "[B]",
    "after_match" => "[A]",
    "chunk_separator" => " ... ",
    "limit" => 100,
    "around" => 2
);

$results = array ();
$results [] = $opts;
$res = $client->BuildExcerpts ( $docs, "test_idx", $words, $opts );
if ( !$res )
{
	$results = false;
	return;
}
$results [] = $res;

$opts = array
(
	"before_match" => "[B]",
    "after_match" => "[A]",
    "chunk_separator" => " ... ",
    "limit" => 50,
    "around" => 2
	);

$results [] = $opts;
$res = $client->BuildExcerpts ( $docs, "test_idx", $words, $opts );
if ( !$res )
{
	$results = false;
	return;
}
$results [] = $res;

$opts = array
(
	"before_match" => "[B]",
    "after_match" => "[A]",
    "chunk_separator" => " ... ",
    "limit" => 30,
    "around" => 2
);

$results [] = $opts;
$res = $client->BuildExcerpts ( $docs, "test_idx", $words, $opts );
if ( !$res )
{
	$results = false;
	return;
}
$results [] = $res;
