test.config:
indexer
{
	mem_limit		= 16M
}

searchd
{

	grouping_in_utc = 1
}

source src
{
	type			= mysql

	sql_query		= SELECT * FROM test_table
	sql_attr_uint	= tm
}

source src_bigint
{
type			= mysql

sql_query		= SELECT * FROM test_table_bigint
sql_attr_bigint	= tm
}

index idx
{
	source			= src
	path			= /main
}

index idx_bigint
{
source			= src_bigint
path			= /main_bigint
}

queries.sphinxql:
select tm, day(tm) d from idx where match('aaaa') group by d;
select tm, month(tm) d from idx where match('aaaa') group by d;
select tm, year(tm) d from idx where match('aaaa') group by d;
select tm, yearmonth(tm) d from idx where match('aaaa') group by d;
select tm, yearmonthday(tm) d from idx where match('aaaa') group by d;

show variables like 'grouping%';
set global grouping_in_utc=0;
show variables like 'grouping_in_utc';
set global grouping_in_utc=1;

select tm, day(tm) d from idx where match('aaaa') group by d;
select tm, month(tm) d from idx where match('aaaa') group by d;
select tm, year(tm) d from idx where match('aaaa') group by d;
select tm, yearmonth(tm) d from idx where match('aaaa') group by d;
select tm, yearmonthday(tm) d from idx where match('aaaa') group by d;



set global grouping_in_utc=0;
show variables like 'grouping%';
select tm, day(tm) d from idx_bigint where match('aaaa') group by d;
select tm, month(tm) d from idx_bigint where match('aaaa') group by d;
select tm, year(tm) d from idx_bigint where match('aaaa') group by d;
select tm, yearmonth(tm) d from idx_bigint where match('aaaa') group by d;
select tm, yearmonthday(tm) d from idx_bigint where match('aaaa') group by d;


set global grouping_in_utc=1;
show variables like 'grouping_in_utc';

select tm, day(tm) d from idx_bigint where match('aaaa') group by d;
select tm, month(tm) d from idx_bigint where match('aaaa') group by d;
select tm, year(tm) d from idx_bigint where match('aaaa') group by d;
select tm, yearmonth(tm) d from idx_bigint where match('aaaa') group by d;
select tm, yearmonthday(tm) d from idx_bigint where match('aaaa') group by d;
